// Simular dados de sensores industriais
const timestamp = new Date().toISOString();
const baseTemp = 75; // Temperatura base
const basePressure = 2.5; // Pressão base
const baseFlow = 150; // Vazão base
const baseLevel = 60; // Nível base

// Adicionar variação aleatória
const tempVariation = (Math.random() - 0.5) * 10;
const pressureVariation = (Math.random() - 0.5) * 0.5;
const flowVariation = (Math.random() - 0.5) * 30;
const levelVariation = (Math.random() - 0.5) * 20;

// Dados dos sensores
const sensorData = {
    timestamp: timestamp,
    sensors: {
        temperature: {
            value: Math.round((baseTemp + tempVariation) * 100) / 100,
            unit: "°C",
            location: "Reator Principal",
            status: "OK"
        },
        pressure: {
            value: Math.round((basePressure + pressureVariation) * 100) / 100,
            unit: "bar",
            location: "Linha de Vapor",
            status: "OK"
        },
        flow: {
            value: Math.round(baseFlow + flowVariation),
            unit: "L/min",
            location: "Entrada Principal",
            status: "OK"
        },
        level: {
            value: Math.round(baseLevel + levelVariation),
            unit: "%",
            location: "Tanque 001",
            status: "OK"
        }
    },
    equipment: {
        motor1: {
            name: "Motor Principal",
            status: Math.random() > 0.1 ? "RUNNING" : "STOPPED",
            rpm: Math.round(1750 + (Math.random() - 0.5) * 100),
            power: Math.round((15 + (Math.random() - 0.5) * 3) * 100) / 100,
            temperature: Math.round((65 + (Math.random() - 0.5) * 10) * 100) / 100,
            vibration: {
                overall: Math.round((2.5 + (Math.random() - 0.5) * 1.5) * 100) / 100,
                axial: Math.round((1.2 + (Math.random() - 0.5) * 0.8) * 100) / 100,
                radial_de: Math.round((2.0 + (Math.random() - 0.5) * 1.2) * 100) / 100,
                radial_nde: Math.round((1.8 + (Math.random() - 0.5) * 1.0) * 100) / 100,
                unit: "mm/s RMS",
                frequency_bands: {
                    "1x_rpm": Math.round((0.8 + (Math.random() - 0.5) * 0.6) * 100) / 100,
                    "2x_rpm": Math.round((0.4 + (Math.random() - 0.5) * 0.3) * 100) / 100,
                    "high_freq": Math.round((0.3 + (Math.random() - 0.5) * 0.2) * 100) / 100
                },
                thresholds: {
                    good: 2.8,
                    satisfactory: 7.1,
                    unsatisfactory: 18.0,
                    unacceptable: 45.0
                }
            },
            bearing_condition: {
                de_bearing: Math.random() > 0.95 ? "WARNING" : "GOOD",
                nde_bearing: Math.random() > 0.98 ? "WARNING" : "GOOD",
                temperature_de: Math.round((45 + (Math.random() - 0.5) * 8) * 100) / 100,
                temperature_nde: Math.round((42 + (Math.random() - 0.5) * 6) * 100) / 100
            }
        },
        pump1: {
            status: Math.random() > 0.05 ? "ON" : "MAINTENANCE",
            efficiency: Math.round((85 + (Math.random() - 0.5) * 10) * 100) / 100
        }
    }
};

// Gerar alertas baseados nos valores
const alerts = [];

if (sensorData.sensors.temperature.value > 85) {
    alerts.push({
        level: "CRITICAL",
        message: "Temperatura alta no reator",
        value: sensorData.sensors.temperature.value,
        timestamp: timestamp
    });
}

if (sensorData.sensors.pressure.value > 3.0) {
    alerts.push({
        level: "WARNING",
        message: "Pressão elevada na linha de vapor",
        value: sensorData.sensors.pressure.value,
        timestamp: timestamp
    });
}

if (sensorData.sensors.level.value < 20) {
    alerts.push({
        level: "WARNING",
        message: "Nível baixo no tanque 001",
        value: sensorData.sensors.level.value,
        timestamp: timestamp
    });
}

if (sensorData.equipment.motor1.status === "STOPPED") {
    alerts.push({
        level: "INFO",
        message: "Motor 1 desligado",
        timestamp: timestamp
    });
}

// Alertas de vibração
const vibration = sensorData.equipment.motor1.vibration;
if (vibration.overall > vibration.thresholds.unacceptable) {
    alerts.push({
        level: "CRITICAL",
        message: "Vibração crítica no motor principal",
        value: vibration.overall,
        threshold: vibration.thresholds.unacceptable,
        unit: vibration.unit,
        timestamp: timestamp
    });
} else if (vibration.overall > vibration.thresholds.unsatisfactory) {
    alerts.push({
        level: "WARNING",
        message: "Vibração alta no motor principal",
        value: vibration.overall,
        threshold: vibration.thresholds.unsatisfactory,
        unit: vibration.unit,
        timestamp: timestamp
    });
} else if (vibration.overall > vibration.thresholds.satisfactory) {
    alerts.push({
        level: "INFO",
        message: "Vibração acima do ideal no motor principal",
        value: vibration.overall,
        threshold: vibration.thresholds.satisfactory,
        unit: vibration.unit,
        timestamp: timestamp
    });
}

// Alertas de rolamentos
if (sensorData.equipment.motor1.bearing_condition.de_bearing === "WARNING") {
    alerts.push({
        level: "WARNING",
        message: "Rolamento DE com indicação de desgaste",
        equipment: "motor1",
        component: "bearing_de",
        timestamp: timestamp
    });
}

if (sensorData.equipment.motor1.bearing_condition.nde_bearing === "WARNING") {
    alerts.push({
        level: "WARNING",
        message: "Rolamento NDE com indicação de desgaste",
        equipment: "motor1",
        component: "bearing_nde",
        timestamp: timestamp
    });
}

// Alertas de temperatura de rolamentos
if (sensorData.equipment.motor1.bearing_condition.temperature_de > 60) {
    alerts.push({
        level: "WARNING",
        message: "Temperatura alta no rolamento DE",
        value: sensorData.equipment.motor1.bearing_condition.temperature_de,
        threshold: 60,
        unit: "°C",
        timestamp: timestamp
    });
}

if (sensorData.equipment.motor1.bearing_condition.temperature_nde > 60) {
    alerts.push({
        level: "WARNING",
        message: "Temperatura alta no rolamento NDE",
        value: sensorData.equipment.motor1.bearing_condition.temperature_nde,
        threshold: 60,
        unit: "°C",
        timestamp: timestamp
    });
}

sensorData.alerts = alerts;

// Armazenar dados globalmente para API
global.set("scadaData", sensorData);

msg.payload = sensorData;
return msg;

const data = msg.payload;

// Criar payload estruturado para InfluxDB
const influxData = {
    measurement: 'scada_data',
    tags: {
        timestamp: data.timestamp
    },
    fields: {}
};

// Sensores
Object.keys(data.sensors).forEach(sensorName => {
    const sensor = data.sensors[sensorName];
    influxData.fields[`sensor_${sensorName}`] = sensor.value;
    influxData.tags[`${sensorName}_location`] = sensor.location;
    influxData.tags[`${sensorName}_status`] = sensor.status;
});

// Equipamentos
Object.keys(data.equipment).forEach(equipName => {
    const equip = data.equipment[equipName];

    // Dados básicos do equipamento
    if (equip.rpm) influxData.fields[`${equipName}_rpm`] = equip.rpm;
    if (equip.power) influxData.fields[`${equipName}_power`] = equip.power;
    if (equip.temperature) influxData.fields[`${equipName}_temperature`] = equip.temperature;
    if (equip.status) influxData.tags[`${equipName}_status`] = equip.status;

    // Vibração
    if (equip.vibration) {
        influxData.fields[`${equipName}_vibration_overall`] = equip.vibration.overall;
        influxData.fields[`${equipName}_vibration_axial`] = equip.vibration.axial;
        influxData.fields[`${equipName}_vibration_radial_de`] = equip.vibration.radial_de;
        influxData.fields[`${equipName}_vibration_radial_nde`] = equip.vibration.radial_nde;

        // Bandas de frequência
        if (equip.vibration.frequency_bands) {
            influxData.fields[`${equipName}_vib_1x_rpm`] = equip.vibration.frequency_bands['1x_rpm'];
            influxData.fields[`${equipName}_vib_2x_rpm`] = equip.vibration.frequency_bands['2x_rpm'];
            influxData.fields[`${equipName}_vib_high_freq`] = equip.vibration.frequency_bands['high_freq'];
        }
    }

    // Rolamentos
    if (equip.bearing_condition) {
        influxData.fields[`${equipName}_bearing_temp_de`] = equip.bearing_condition.temperature_de;
        influxData.fields[`${equipName}_bearing_temp_nde`] = equip.bearing_condition.temperature_nde;
    }
});

// Alertas - contar por nível
const alertCounts = { INFO: 0, WARNING: 0, CRITICAL: 0 };
data.alerts.forEach(alert => {
    alertCounts[alert.level]++;
});

influxData.fields['alerts_info'] = alertCounts.INFO;
influxData.fields['alerts_warning'] = alertCounts.WARNING;
influxData.fields['alerts_critical'] = alertCounts.CRITICAL;
influxData.fields['alerts_total'] = data.alerts.length;

// Retornar mensagem formatada
msg.payload = influxData.fields;
msg.measurement = influxData.measurement;
msg.tags = influxData.tags;

return msg;

// Armazenar dados para API
const currentData = msg.payload;

// Manter histórico dos últimos 100 registros
let history = global.get("scadaHistory") || [];
history.push(currentData);

if (history.length > 100) {
    history = history.slice(-100);
}

global.set("scadaHistory", history);

return msg;

const currentData = global.get("scadaData") || {};

msg.payload = {
    status: "success",
    timestamp: new Date().toISOString(),
    data: currentData
};

msg.headers = {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*"
};

return msg;

const history = global.get("scadaHistory") || [];
const limit = parseInt(msg.req.query.limit) || 50;

msg.payload = {
    status: "success",
    timestamp: new Date().toISOString(),
    count: history.length,
    data: history.slice(-limit)
};

msg.headers = {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*"
};

return msg;
